const loader=document.querySelector(".loding"),menuBtn=document.querySelector(".menu-btn"),sideMenu=document.querySelector(".side-menu"),sideMenuOverlay=document.querySelector(".side-menu-overlay"),closeBtn=document.querySelector(".close-button"),navlink=document.querySelectorAll(".nav-link");window.addEventListener("DOMContentLoaded",()=>{loader.classList.add("disable"),setTimeout(()=>{loader.style.display="none"},1e3)}),menuBtn.addEventListener("click",()=>{sideMenu.classList.add("active"),sideMenuOverlay.classList.add("active")}),closeBtn.addEventListener("click",()=>{sideMenu.classList.remove("active"),sideMenuOverlay.classList.remove("active")}),navlink.forEach(e=>{e.addEventListener("click",()=>{setTimeout(()=>{sideMenu.classList.remove("active"),sideMenuOverlay.classList.remove("active")},1e3)})});